import React, { useState } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  Box,
  useScrollTrigger,
  Slide,
  Container,
} from '@mui/material';

interface Props {
  children: React.ReactElement;
}

function HideOnScroll(props: Props) {
  const { children } = props;
  const trigger = useScrollTrigger();

  return (
    <Slide appear={false} direction="down" in={!trigger}>
      {children}
    </Slide>
  );
}

const Navbar = () => {
  const [activeSection, setActiveSection] = useState('home');

  const scrollToSection = (sectionId: string) => {
    setActiveSection(sectionId);
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <HideOnScroll>
      <AppBar position="fixed" sx={{ background: 'rgba(255, 255, 255, 0.8)', backdropFilter: 'blur(8px)' }}>
        <Container maxWidth="lg">
          <Toolbar disableGutters>
            <Typography
              variant="h6"
              component="div"
              sx={{ flexGrow: 1, fontWeight: 700 }}
            >
              My Portfolio
            </Typography>
            <Box sx={{ display: 'flex', gap: 2 }}>
              <Button
                color="inherit"
                onClick={() => scrollToSection('home')}
                sx={{
                  color: activeSection === 'home' ? 'primary.main' : 'inherit',
                  fontWeight: activeSection === 'home' ? 600 : 400,
                }}
              >
                Home
              </Button>
              <Button
                color="inherit"
                onClick={() => scrollToSection('results')}
                sx={{
                  color: activeSection === 'results' ? 'primary.main' : 'inherit',
                  fontWeight: activeSection === 'results' ? 600 : 400,
                }}
              >
                Results
              </Button>
              <Button
                color="inherit"
                onClick={() => scrollToSection('hobbies')}
                sx={{
                  color: activeSection === 'hobbies' ? 'primary.main' : 'inherit',
                  fontWeight: activeSection === 'hobbies' ? 600 : 400,
                }}
              >
                Hobbies
              </Button>
              <Button
                color="inherit"
                onClick={() => scrollToSection('login')}
                sx={{
                  color: activeSection === 'login' ? 'primary.main' : 'inherit',
                  fontWeight: activeSection === 'login' ? 600 : 400,
                }}
              >
                Login
              </Button>
            </Box>
          </Toolbar>
        </Container>
      </AppBar>
    </HideOnScroll>
  );
};

export default Navbar;
